import React, { Component } from 'react'
import { Feed, Icon, Segment, Form, Input, TextArea, Label, Button } from 'semantic-ui-react'

import {postComment } from '../../../../actions/storyActions';

import {connect} from 'react-redux'

class Comments extends Component {

  state = {text:'',
  comments:[],
}

handleChange = event =>{
  this.setState({[event.target.name]:event.target.value});
}

componentWillReceiveProps(nextProps){
  if(nextProps.comment){
    this.setState({comments: this.state.comments.unshift(nextProps.comment)});
  }
}
componentWillMount(){
  this.setState({comments:this.props.comments});
}
  onSubmit=()=>{

    const commentData = {
      text:this.state.text
    };
    this.props.postComment(this.props.storyID,commentData)
  }

  render() {
    const {text, comments} = this.state;

    return (
      <Segment>
  
  {comments &&

      comments.map(comment=>
        <div>
        <Feed >
    <Feed.Event>
      <Feed.Label>
        <img src={comment.avatar}/>
      </Feed.Label>
      <Feed.Content>
        <Feed.Summary>
          <Feed.User>{comment.name? comment.name : 'Anonynomous'}</Feed.User> commented
          <Feed.Date>{comment.date}</Feed.Date>
          <Feed.Extra text>{comment.text}</Feed.Extra>
        </Feed.Summary>
       
      </Feed.Content>
    </Feed.Event>

    
  </Feed>

  </div>
      )}


          
<Form>

<Label>Post Your Comment!
<TextArea maxLength="300" name='text' placeholder='Tell us what you think'   value={text} onChange={this.handleChange}/>
<Button floated='right' positive onClick = {()=>{this.onSubmit()}}> Post </Button>
</Label>
</Form>
  </Segment>    
          
  
  )
  }
}
const mapStateToProps = state => ({

  comment: state.stories.comment,
});


export default connect(mapStateToProps, {postComment})(Comments);
