import React, { Component } from 'react'
import { Message, Dropdown } from 'semantic-ui-react'
import { connect } from 'react-redux';
import { getCategories } from '../../actions/storyActions';


class NewStory extends Component {
  state = { activeItem: 'home' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })
  
  componentWillMount()
  {
    this.props.getCategories();

  }

  render() {
    const { activeItem } = this.state

    return (
      <div>
        <Message>MEMES</Message>
        <Dropdown text='File'>
    <Dropdown.Menu>
      <Dropdown.Item text='New' />
      <Dropdown.Item text='Open...' description='ctrl + o' />
      <Dropdown.Item text='Save as...' description='ctrl + s' />
      <Dropdown.Item text='Rename' description='ctrl + r' />
      <Dropdown.Item text='Make a copy' />
      <Dropdown.Item icon='folder' text='Move to folder' />
      <Dropdown.Item icon='trash' text='Move to trash' />
      <Dropdown.Divider />
      <Dropdown.Item text='Download As...' />
      <Dropdown.Item text='Publish To Web' />
      <Dropdown.Item text='E-mail Collaborators' />
    </Dropdown.Menu>
  </Dropdown>
      
  </div>
       
   
    )
  }
}

const mapStateToProps = state => ({
    auth: state.auth,
    categories: state.stories.categories,
  });
  
  
  
  export default connect(mapStateToProps, {getCategories})(NewStory);
