import React, { Component } from 'react'
import StoryDetailOwner from './StoryDetailOwner/StoryDetailOwner';
import StoryDetailViewer from './StoryDetailViewer/StoryDetailViewer';
import Spinner from '../Utils/Spinner';
import { connect } from 'react-redux';
import {  Message } from 'semantic-ui-react'

import { getStory } from '../../actions/storyActions';

import Navbar from '../Navbar/Navbar';



class StoryDetail extends Component {

  state = {
    isLoading:true,
    authId:'',
    storyId:'0',

  }

  componentWillMount(){

    this.props.getStory(this.props.match.params.id);  
  }

  componentWillReceiveProps(nextProps){
  
    this.setState({authId:this.props.auth.user.id, storyId:nextProps.stories.story.user.id});
    
  }


  render() {
    const{authId,storyId} = this.state;
    return (
      <div>
          <Navbar history={this.props.history}/>

          {({authId} && {storyId})&&
          //if user is owner they see extra stuff like ability to add more events
          authId!==storyId?<div>
                        {({authId})&&<Message>Auth ID: {authId} and Story User ID: {storyId}</Message>}

          <StoryDetailViewer   />
          
          </div>:
          <div>
            {({authId})&&<Message>Auth ID: {authId} and Story User ID: {storyId}</Message>}
          <StoryDetailOwner />
          </div>
          }
      

      </div>
    ) 
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  story: state.stories.story,
  isLoading: state.stories.isLoading,
});


export default connect(mapStateToProps, {getStory})(StoryDetail);
