//Shows Profile by handle.

import React, { Component } from 'react'
import { Grid, Image, Label, Segment, Container, Card,Menu } from 'semantic-ui-react'


import { connect } from 'react-redux';
import { getStoriesByProfile} from '../../actions/storyActions';

 class ProfileViewer extends Component {


  componentWillMount(){
    alert("HANDLE: " + this.props.match.params.handle);
    this.props.getStoriesByProfile(this.props.match.params.handle);  

  }

  render() {

    return (
      
      <Grid columns={2}>
      <Grid.Column>
        <Segment raised>
    
        </Segment>
      </Grid.Column>
    </Grid>

    )
  }
}


const mapStateToProps = state => ({

  profileStories: state.stories.profileStories,
});



export default connect(mapStateToProps, {getStoriesByProfile})(ProfileViewer);
