import isEmpty from '../validation/is-empty';

import { GET_STORIES } from '../actions/types';

const initialState = {
  isAuthenticated: false,
  user: {}
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_STORIES:
      return {
        ...state,
        stories: action.payload
      };
    default:
      return state;
  }
}
