import React, { Component } from 'react'
import { Message, Dropdown, Segment,Header,Button, Grid, Form, Icon, Label } from 'semantic-ui-react'
import { connect } from 'react-redux';
import {Link} from 'react-router-dom';
import { getCategories } from '../../actions/storyActions';


class NewStory extends Component {
  state = { activeItem: 'home',
}

  handleItemClick = (e, { name }) => 
  

  
  {
      alert("MEMES");
      this.setState({ activeItem: name });

      alert("NAME: " + name);
}
  componentWillMount()
  {
    this.props.getCategories();

  }

  handleCategorySelect = (subcategory) =>{
    
    alert(subcategory);
  }

  render() {
    const { activeItem } = this.state

    return (
        <div style={{height:'100vh'}}>         
        <Grid textAlign = 'center' verticalAlign ='middle' className='app'>
            <Grid.Column style = {{maxWidth:450}}>
                <Header as='h2' icon color ='olive' textAlign = 'center'>
                    <Icon name = 'lightbulb outline' color = 'olive'/>
                    Upload Your Story!
                </Header>
                <Form onSubmit = {this.handleSubmit}>
                    <Segment stacked >
                        <Form.Input fluid name='title' icon='user' iconPosition='left' 
                        placeholder ='Title' onChange = {this.handleChange} type = 'text'
                        value = {this.state.name}   maxLength="30"
                        />
            
                         <Form.TextArea fluid name='description' icon='mail' iconPosition='left' 
                        placeholder ='Description' onChange = {this.handleChange} type = 'text'
                        value = {this.state.email} maxLength="300"/>

                        
                        
                        <Form.Input fluid name='password2' icon='repeat' iconPosition='left' 
                        placeholder ='Password Confirmation' onChange = {this.handleChange} type = 'password'
                        value = {this.state.password2}><Dropdown text='Category' scrolling selection>
                        
                        <Dropdown.Menu >
                        {
                            this.props.categories? 
                            
                            this.props.categories.map((category)=>{
                                return(
                                <React.Fragment>
                                    
                                <Dropdown.Header>{category.title}</Dropdown.Header>
                
                                {
                                    category.subcategories.map((subcategory)=>{
                                    return(
                                        <Dropdown.Item onClick={()=>subcategory} text={subcategory} />
                                    );
                                })
                                }
                
                               
                            
                                <Dropdown.Divider />
                              
                                
                
                                </React.Fragment>
                                );
                              })
                              : null
                
                        }
                      
                       </Dropdown.Menu>  
                       
                  </Dropdown>
                  <Label color='red' style={{marginLeft:'15px'}}>Please Choose A Category</Label>

</Form.Input>
                        

                        <Button color = 'olive' fluid size = 'large'>Submit</Button>
                    </Segment>

                </Form>
                <Message color='olive'>Already a User? <Link to='/Login'>Login</Link></Message>
            </Grid.Column>
        </Grid>
    </div>


   
    )
  }
}

const mapStateToProps = state => ({
    auth: state.auth,
    categories: state.stories.categories,
  });
  
  
  
  export default connect(mapStateToProps, {getCategories})(NewStory);
