import isEmpty from '../validation/is-empty';

import { GET_STORIES, GET_CATEGORIES, POST_STORY, GET_STORY, POST_FAVORITE } from '../actions/types';

const initialState = {
  isLoading:true,
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_STORIES:
      return {
        ...state,
        storiesList: action.payload,
        isLoading: false,

      };
    case GET_CATEGORIES:
      return{
        ...state,
        categories: action.payload,
      };
    case POST_STORY:
      return{
        ...state,
        story: action.payload,
        isLoading:false,
        
      }
      case GET_STORY:
      return{
        ...state,
        story: action.payload,
        isLoading:false,
        
      }
      case POST_FAVORITE:
      return{
        ...state,
        story: action.payload,
        
      }
    default:
      return state;
  }
}
