    return (
      <div>

         <Header as='h2'>
           <Header.Content>asdsadasd</Header.Content>
           <br/>
         <Image 
    style={{height:'250px', width:'250px'}}  circular src='http://localhost:5000/images/progress.png' />
              <br/>
              <br/>
         {owner&& 
        
         <Button color='teal'> Change Image</Button>
    }
    <Divider horizontal style ={{color:'white'}}> About </Divider>
  
    <Header.Subheader style={{color:'white'}}>sadsad sdsa dsa sad sad sadsa dsa dsad sadsad sad d sadsad</Header.Subheader>
