//Shows Profile by handle.

import React, { Component } from 'react'
import { Grid, Image, Label, Segment, Container } from 'semantic-ui-react'


import { connect } from 'react-redux';
import { getStoriesByProfile} from '../../actions/profileActions';

 class ProfileViewer extends Component {


  componentWillMount(){
    alert("HANDLE: " + this.props.match.params.handle);
    this.props.getStoriesByProfile(this.props.match.params.handle);  

  }

  render() {

    return (
      
      <Grid columns={2}>
      <Grid.Column>
        <Segment raised>
          <Label as='a' color='red' ribbon>
            Overview
          </Label>
          <span>Account Details</span>
  
          <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
  
          <Label as='a' color='blue' ribbon>
            Community
          </Label>
          <span>User Reviews</span>
  
          <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
        </Segment>
      </Grid.Column>
  
      <Grid.Column>
        <Segment>
          <Label as='a' color='orange' ribbon='right'>
            Specs
          </Label>
          <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
  
          <Label as='a' color='teal' ribbon='right'>
            Reviews
          </Label>
          <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
        </Segment>
      </Grid.Column>
    </Grid>

    )
  }
}


const mapStateToProps = state => ({

  profileStories: state.stories.profileStories,
});



export default connect(mapStateToProps, {getStoriesByProfile})(ProfileViewer);
