import axios from 'axios';
import setAuthToken from '../utils/setAuthToken';
import jwt_decode from 'jwt-decode';

import { GET_ERRORS, GET_PROFILE } from './types';




// GET user profile
export const getProfile = () => dispatch => {

    axios
      .get('/api/profile/')
      .then(res => {

       

          dispatch({
          type:GET_PROFILE,
          payload: res.data,
          })})
      .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
      );
  };
