import axios from 'axios';
import setAuthToken from '../utils/setAuthToken';
import jwt_decode from 'jwt-decode';

import { GET_ERRORS, GET_STORIES } from './types';

// GET Stories
export const getStories = () => dispatch => {
  axios
    .get('/api/stories')
    .then(res => 
        dispatch({
        type:GET_STORIES,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};
