import React, { Component } from 'react'
import { Card, Container, Header, Label, Image, Divider, Icon, Form, Grid, Segment, Button} from 'semantic-ui-react'
import Events from '../Sections/Events/Events';
import Comments from '../Sections/Comments/Comments';
import Statistics from '../Sections/Statistics/Statistics';

export default class StoryDetailViewer extends Component {
  state={
    events:[],
    comments:[],
    favorites:0,
    views: 0,
   
  }

  componentWillReceiveProps(nextProps){
    alert(JSON.stringify(nextProps));
    alert("NEXT PROPS VIEWS: " + nextProps.views);
    this.setState({
      events:nextProps.events,
      comments: nextProps.comments,
      views: nextProps.views,

    });

  }
 
  
  render() {
    return (
    <Container>
        <Divider style={{color:'white'}} horizontal color='white'> Events(3)</Divider>
        <Events/>
        <Statistics/>
        
        <Divider  style={{color:'white'}} horizontal color='white'> Comments (24) </Divider>
        <Comments/>
    </Container>

    )
  }
}
