import React, { Component } from 'react'
import {   Header,Image, Button, Divider, } from 'semantic-ui-react'

import { connect } from 'react-redux';

import { postFavorite, postUnfavorite, uploadStoryImage } from '../../../../actions/storyActions';


class Description extends Component {
    state = {
        owner:true,
    }

  render() {
      const {owner}= this.state;
    return (
      <div>

         <Header as='h2'>
           <Header.Content style ={{color:'white'}}>asdsadasd</Header.Content>
           <br/>
         <Image 
    style={{height:'250px', width:'250px'}}  circular src='http://localhost:5000/images/progress.png' />
              <br/>
              <br/>
         {owner&&<React.Fragment>
        <input             
        type='file'
        style={{display:'none'}}

        onChange = {this.fileSelectedHandler}
        ref={fileInput=>{this.fileInput = fileInput}}
        >
           </input>

        <Button
          
           floated='left'
           type='file'
           positive
           icon='arrow alternate circle up outline'
           labelPosition='right'
           content="Pick A File"
           size='large'
           onClick={()=>this.fileInput.click()}
           />
        <Button
          onClick={this.fileUploadHandler} 
          content="Upload"
          floated='left'
          size='large'
        
        />
        </React.Fragment>
        
    }
    <Divider horizontal style ={{color:'white'}}> About </Divider>
  
    <Header.Subheader style={{color:'white'}}>sadsad sdsa dsa sad sad sadsa dsa dsad sadsad sad d sadsad</Header.Subheader>
  </Header>
      </div>
    )
  }
}

const mapStateToProps = state => ({
    auth: state.auth,
    story:  state.stories.story,
    isLoading: state.stories.isLoading,
  });
  
  export default connect(mapStateToProps, {postFavorite, postUnfavorite, uploadStoryImage})(Description);
