import React from "react";
import { Loader, Dimmer } from "semantic-ui-react";

const Spinner = () => (
  <Dimmer active inverted>
    <Loader style={{height:'100%'}} inverted size="huge" content={"Loading..."} />
  </Dimmer>
);

export default Spinner;
