import React, { Component } from 'react'
import { Menu, Label, Button, Icon, Dropdown } from 'semantic-ui-react'
import { connect } from 'react-redux';
import { getCategories } from '../../actions/storyActions';


class MenuExampleInverted extends Component {
  state = { activeItem: 'home' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })
  
  componentWillMount()
  {
    this.props.getCategories();

  }

  render() {
    const { activeItem } = this.state

    return (
      <Menu inverted size='large' stackable > 
        <Menu.Item name='progress' active={activeItem === 'progress'} onClick={this.handleItemClick} />
        <Menu.Item 
          
        >
        <Dropdown text='Categories' color='teal'>
    <Dropdown.Menu>
      <Dropdown.Header>Development</Dropdown.Header>
      <Dropdown.Item text='Programming'  />
  

      <Dropdown.Divider />
      <Dropdown.Header>Life</Dropdown.Header>

      <Dropdown.Item text='Fitness' />
      <Dropdown.Item text='Cooking' />
      <Dropdown.Item text='Education' />

      <Dropdown.Divider />
      <Dropdown.Header>Art</Dropdown.Header>

      <Dropdown.Item text='2D Art' />
      <Dropdown.Item text='3D Art' />
      <Dropdown.Item text='Cinema' />
      <Dropdown.Item text='Pixel Art' />
      <Dropdown.Item text='Cinema' />


    </Dropdown.Menu>

  </Dropdown>
        </Menu.Item>
        <Menu.Item
          style={{backgroundColor:'green', border: "2px solid lightgreen",
          borderRadius: "15px"}}
          name='Upload Story'
          active={activeItem === 'Upload Story'}
          onClick={this.handleItemClick}
          color='olive'
          icon='plus'
          
        />

   
        <Menu.Item
          name='login'
          active={activeItem === 'login'}
          onClick={this.handleItemClick}
          color='blue'
          position='right'
        />
        <Menu.Item
          
          name='register'
          active={activeItem === 'register'}
          onClick={this.handleItemClick}
          color='red'

        />

      </Menu>
    )
  }
}

const mapStateToProps = state => ({
    auth: state.auth,
    stories: state.stories.storiesList,
  });
  
  
  
  export default connect(mapStateToProps, {getCategories})(MenuExampleInverted);
