import React, { Component }  from 'react'
import { Grid, Image, Label, Card, Icon, Menu, Segment, Container, Button } from 'semantic-ui-react'
import "typeface-roboto";
import "./Landing.css"
import { connect } from 'react-redux';
import { getStories } from '../../actions/storyActions';


const testClick = () =>{
  alert("TEST CLICK");
}


 class Landing extends Component {

  componentWillMount()
  {
    this.props.getStories();

  }


  render() {

    return (
      <div>
        <Container  >

        <Card.Group itemsPerRow={4} stackable>
  


      {this.props.stories.map(story=>
        <Card  onClick={()=>testClick()} fluid style = {{  backgroundColor: "#4c4c4c", boxShadow:"none"}} raised >  
        <Image src='http://localhost:5000/images/1556324419626Bird.jpg' width={'100%'} height={200}/>
    
        <Card.Content  fluid   >
          
          <Card.Header style = {{color:'white'}}> I got my Bachelors Degree! </Card.Header>
          <Card.Meta>
            
            <Label color='olive'>dev</Label>
            <Label >programming</Label>
    
          </Card.Meta>
          
          <Card.Description  style = {{color:'#7a7a7a '}} >Matthew is a musician living in Nashville.</Card.Description>
        </Card.Content>
     
        <Menu  fluid widths={3} text>
            <Menu.Item style = {{color:'gray'}}  name='24'  icon='heart'/>
            <Menu.Item style = {{color:'gray'}} name='12'  icon='comment'/>
            <Menu.Item style = {{color:'gray'}}name='120'  icon='eye'/>
    
          </Menu>
    
        
      </Card>


      )}
    
 



</Card.Group>



</Container>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  stories: state.stories,
});



export default connect(mapStateToProps, {getStories})(Landing);
