import axios from 'axios';
import setAuthToken from '../utils/setAuthToken';
import jwt_decode from 'jwt-decode';

import { GET_ERRORS, GET_STORIES, GET_CATEGORIES, POST_STORY, POST_EVENT, GET_STORY, POST_FAVORITE, POST_UNFAVORITE, POST_COMMENT } from './types';

// GET Stories
export const getStories = () => dispatch => {
  axios
    .get('/api/stories')
    .then(res => 
        dispatch({
        type:GET_STORIES,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};


// GET Stories
export const getCategories = () => dispatch => {
  axios
    .get('/api/stories/utils/categories')
    .then(res => 
        dispatch({
        type:GET_CATEGORIES,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};


// POST a story
export const postStory = (postData) => dispatch => {
  axios
    .post('/api/stories',postData)
    .then(res => 
        dispatch({
        type:POST_STORY,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};

// POST a story
export const postComment = (storyID,commentData) => dispatch => {
  axios
    .post(`/api/stories/comment/${storyID}`,commentData)
    .then(res => 
        dispatch({
        type:POST_COMMENT,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};

// GET a story by id
export const getStory = (storyID) => dispatch => {
  axios
    .get(`/api/stories/${storyID}`)
    .then(res => {
        dispatch({
        type:GET_STORY,
        payload: res.data,

        })})
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};


// POST a favorite to a post
export const postFavorite = (postID) => dispatch => {
  axios
    .post(`/api/stories/favorite/${postID}`)
    .then(res => 
        dispatch({
        type:POST_FAVORITE,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};


// POST a favorite to a post
export const postUnfavorite = (postID) => dispatch => {
  axios
    .post(`/api/stories/unfavorite/${postID}`)
    .then(res => 
        dispatch({
        type:POST_UNFAVORITE,
        payload: res.data,
        }))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};
