import React, { Component } from 'react'
import { Feed, Icon, Segment, Form, Input, TextArea, Label, Button } from 'semantic-ui-react'

import {postComment } from '../../../../actions/storyActions';

import {connect} from 'react-redux'

class Comments extends Component {

  state = {text:''
}

handleChange = event =>{
  this.setState({[event.target.name]:event.target.value});
}


  onSubmit=(e)=>{
    e.preventDefault();

    const commentData = this.state.text;

    this.props.postComment(commentData)
  }

  render() {
    const {text} = this.state;

    return (
      <Segment>
  
  {this.props.comments &&

      this.props.comments.map(comment=>
        <div>
        <Feed >
    <Feed.Event>
      <Feed.Label>
        <img src={comment.avatar}/>
      </Feed.Label>
      <Feed.Content>
        <Feed.Summary>
          <Feed.User>{comment.name? comment.name : 'Anonynomous'}</Feed.User> commented
          <Feed.Date>{comment.date}</Feed.Date>
          <Feed.Extra text>{comment.text}</Feed.Extra>
        </Feed.Summary>
       
      </Feed.Content>
    </Feed.Event>

    
  </Feed>

  </div>
      )}


          
<Form>

<Label>Post Your Comment!
<TextArea maxLength="300" placeholder='Tell us what you think'  value={this.state.text} onChange={this.handleChange}/>
<Button floated='right' positive> Post </Button>
</Label>
</Form>
  </Segment>    
          
  
  )
  }
}



export default connect(null, {postComment})(Comments);
