import axios from 'axios';
import setAuthToken from '../utils/setAuthToken';
import jwt_decode from 'jwt-decode';

import { GET_ERRORS, GET_PROFILE, GET_PROFILE_HANDLE } from './types';




// GET user profile
export const getProfile = () => dispatch => {

    axios
      .get('/api/profile/')
      .then(res => {

       

          dispatch({
          type:GET_PROFILE,
          payload: res.data,
          })})
      .catch(err =>
        dispatch({
          type: GET_ERRORS,
          payload: err.response.data
        })
      );
  };

  
// GET user profile
export const getProfileByHandle = (handle) => dispatch => {

  axios
    .get(`/api/profile/handle/${handle}`)
    .then(res => {

        dispatch({
        type:GET_PROFILE,
        payload: res.data,
        })})
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};
