import React, { Component } from 'react'
import { Message, Dropdown } from 'semantic-ui-react'
import { connect } from 'react-redux';
import { getCategories } from '../../actions/storyActions';


class NewStory extends Component {
  state = { activeItem: 'home' }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })
  
  componentWillMount()
  {
    this.props.getCategories();

  }

  render() {
    const { activeItem } = this.state

    return (
      <div>
        <Message>MEMES</Message>
      <Dropdown  text='Categories'  >
        <Dropdown.Menu>
        {
            this.props.categories? 
            
            this.props.categories.map((category)=>{
                return(
                <React.Fragment>
                    
                <Dropdown.Header>{category.title}</Dropdown.Header>

                {
                    category.subcategories.map((subcategory)=>{
                    return(
                        <Dropdown.Item text={subcategory} />
                    );
                })
                }

               
            
                <Dropdown.Divider />
              
                

                </React.Fragment>
                );
              })
              : null

        }
      
       </Dropdown.Menu>  
       
  </Dropdown>
      
  </div>
       
   
    )
  }
}

const mapStateToProps = state => ({
    auth: state.auth,
    categories: state.stories.categories,
  });
  
  
  
  export default connect(mapStateToProps, {getCategories})(NewStory);
