//Shows Profile by handle.

import React, { Component } from 'react'
import { Grid, Image, Label, Segment, Container, Card,Menu } from 'semantic-ui-react'


import { connect } from 'react-redux';
import { getStoriesByProfile} from '../../actions/storyActions';

 class ProfileViewer extends Component {


  componentWillMount(){
    alert("HANDLE: " + this.props.match.params.handle);
    this.props.getStoriesByProfile(this.props.match.params.handle);  

  }

  render() {

    return (
      
      <Container  >

      <Card.Group itemsPerRow={4} stackable>

        <Segment raised>
        {
          this.props.profileStories? this.props.profileStories.map (story=>
        <Card  onClick={()=>this.selectStory(story._id)} fluid style = {{  backgroundColor: "#4c4c4c", boxShadow:"none"}} raised >  
        <Image src= {story.image} width={'100%'} height={200}/>
    
        <Card.Content  fluid   >
          
          <Card.Header style = {{color:'white'}}> {story.title}</Card.Header>
          <Card.Meta>
            
            <Label color='olive'>{story.category}</Label>
            <Label >{story.subcategory}</Label>
    
          </Card.Meta>
          
          <Card.Description  style = {{color:'#7a7a7a '}} >{story.description}</Card.Description>
        </Card.Content>
     
        <Menu  fluid widths={3} text>
            <Menu.Item style = {{color:'gray'}}  name='24'  icon='heart'/>
            <Menu.Item style = {{color:'gray'}} name='12'  icon='comment'/>
            <Menu.Item style = {{color:'gray'}}name='120'  icon='eye'/>
    
          </Menu>
    
        
      </Card>

        )
        : null}
        </Segment> 
      </Card.Group>
      </Container>
    )
  }
}


const mapStateToProps = state => ({

  profileStories: state.stories.profileStories,
});



export default connect(mapStateToProps, {getStoriesByProfile})(ProfileViewer);
